import { useState } from "react";
import Axios from 'axios';

import Button from "@mui/material/Button";
import TextField from '@mui/material/TextField';

export default function InputBookForm(props) {

  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [location, setLocation] = useState('');

  const submitToDB = () => {

    if (title && author && location) {
      console.log(`submitting : \n${title}\n${author}\n${location}`);
      Axios.post("http://localhost:3001/api/insert", { title, author, location }
      ).then(() => {
        alert('successful insert');
      });

      window.location.reload();
    }
    else {
      console.log('blank field submit error')
    }


  };

  return (
    <div className='TextFieldBookForm'>

      <h3>Add new book to db</h3>

      <TextField label='title' onChange={(e) => setTitle(e.target.value)}></TextField>
      <TextField label='author' onChange={(e) => setAuthor(e.target.value)}></TextField>
      <TextField label='location' onChange={(e) => setLocation(e.target.value)}></TextField>

      <Button
        color='primary'
        variant='contained'
        onClick={submitToDB}
      >submit
      </Button>
    </div>
  );

}
