import { useState, useEffect } from "react";
import Axios from 'axios';

function App() {

  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [location, setLocation] = useState('');
  const [booksList, setBooksList] = useState([]);

  useEffect(() => {
    Axios.get('http://localhost:3001/api/get').then((response) => {
      //console.log(response.data);
      setBooksList(response.data);
      console.log(`booksList: ${booksList}`);
    })
  }, [getBooks]);

  const submitToDB = () => {

    console.log(`submitting : \n${title}\n${author}\n${location}`);

    Axios.post("http://localhost:3001/api/insert", { title, author, location }
    ).then(() => {
      alert('successful insert');
    });

    setGetBooks(!getBooks);
    window.location.reload();
  };

  return (
    <div className="App">
      <h1>library</h1>

      <div className='InputBookForm'>
        <input type='text' placeholder='title' onChange={(e) => setTitle(e.target.value)}></input>
        <input type='text' placeholder='author' onChange={(e) => setAuthor(e.target.value)}></input>
        <input type='text' placeholder='location' onChange={(e) => setLocation(e.target.value)}></input>

        <button onClick={submitToDB}>submit</button>
      </div>

      <div className={`displayBooks ${getBooks}`}>
        {booksList.map(book => {
          {/* if(book.isbn === booksList.length){
            return (<h1>{book.title}</h1>);
          } */}
          return (
            <div>
            <h1>{book.title}</h1>
            <h2>by {book.author}</h2>
            <h3>isbn: {book.isbn} location: {book.location}</h3>
            </div>
          )
          })
        }
      </div>

    </div>
  );
}

export default App;
