import { useState } from "react";
import Axios from 'axios';

import Button from "@mui/material/Button";
import TextField from '@mui/material/TextField';

export default function InputBookForm(props) {

  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [location, setLocation] = useState('');

  const submitToDB = () => {

    console.log(`submitting : \n${title}\n${author}\n${location}`);

    Axios.post("http://localhost:3001/api/insert", { title, author, location }
    ).then(() => {
      alert('successful insert');
    });

    window.location.reload();
  };

  return (
    <div className='TextFieldBookForm'>
      <TextField label='title' onChange={(e) => setTitle(e.target.value)}></TextField>
      <TextField label='author' onChange={(e) => setAuthor(e.target.value)}></TextField>
      <TextField label='location' onChange={(e) => setLocation(e.target.value)}></TextField>

      <Button onClick={submitToDB}>submit</Button>
    </div>
  );

}
