import Axios from 'axios';
import { useEffect, useState } from 'react';
import Book from './Book'

import Button from "@mui/material/Button";
import TextField from '@mui/material/TextField';

export default function DisplayBooks(props) {

  const [booksList, setBooksList] = useState([]);
  const [newLocation, setNewLocation] = useState('');
  //const [changeLocation, setChangeLocation] = useState(false);

  useEffect(() => {
    Axios.get('http://localhost:3001/api/get').then((response) => {
      //console.log(response.data);
      setBooksList(response.data);
      console.log(`booksList: ${booksList}`);
    })
  }, []);

  const changeLocationOnClick = (isbn) => {
    Axios.put('http://localhost:3001/api/update', { newLocation, isbn });
    setNewLocation('');
    window.location.reload();
  };

  const deleteBook = (isbn) => {
    Axios.delete(`http://localhost:3001/api/delete/`, {
      headers: {},
      data: {
        isbn: isbn
      }
    });
    window.location.reload();
  }

  return (
    <div className={`displayBooks`}>
      {booksList.map(book => {
        (<div className='bookContainer'>
            <Book title={book.title} author={book.author} isbn={book.isbn} location={book.location} key={book.isbn} />
            <TextField 
              type='text' 
              label='new location' 
              onChange={(e) => setNewLocation(e.target.value)}>
            </TextField>
            <Button 
              color='secondary' 
              variant='contained'
              onClick={() => changeLocationOnClick(book.isbn)}
              >update location
            </Button>
            <Button 
              color='warning'
              variant='contained' 
              onClick={() => deleteBook(book.isbn)}
              >delete
            </Button>
          </div>)
      })
      }
    </div>
  );
}
