import Axios from 'axios';
import { useEffect, useState } from 'react';
import Book from './Book'

export default function DisplayBooks() {

  const [booksList, setBooksList] = useState([]);
  const [newLocation, setNewLocation] = useState('');
  //const [changeLocation, setChangeLocation] = useState(false);

  useEffect(() => {
    Axios.get('http://localhost:3001/api/get').then((response) => {
      //console.log(response.data);
      setBooksList(response.data);
      console.log(`booksList: ${booksList}`);
    })
  }, []);

  const changeLocationOnClick = (isbn) => {
    Axios.put('http://localhost:3001/api/update', {newLocation, isbn});
    setNewLocation('');
    window.location.reload();
  };

  const deleteBook = (isbn) => {
    headers = {};
    data = {isbn : isbn}
    Axios.delete(`http://localhost:3001/api/delete/`,{headers, data});
    window.location.reload();
  }

  return (
    <div className={`displayBooks`}>
      {booksList.map(book => {
        {/* if(book.isbn === booksList.length){
            return (<h1>{book.title}</h1>);
          } */}
        return (
          <div className='bookContainer'>
            <Book title={book.title} author={book.author} isbn={book.isbn} location={book.location} key={book.isbn}/>
            <input type='text' placeholder='new location' onChange={(e) => setNewLocation(e.target.value)}></input>
            <button onClick={() => changeLocationOnClick(book.isbn)}>update location</button>
            <button onClick={() => deleteBook(book.isbn)}>delete</button>
          </div>
        )
      })
      }
    </div>
  );
}
